import React, { useState } from 'react';
import './App.css'; // You can create this file for additional styling if needed

function App() {
  const [angle, setAngle] = useState(0); // State to hold the angle

  const handleMouseMove = (e) => {
    const { clientX, clientY } = e;
    const centerX = window.innerWidth / 2; // X coordinate of the center
    const centerY = window.innerHeight / 2; // Y coordinate of the center

    // Calculate the angle in radians
    const deltaX = clientX - centerX;
    const deltaY = clientY - centerY;
    const radians = Math.atan2(deltaY, deltaX);

    // Convert radians to degrees
    const degrees = (radians * 180) / Math.PI;

    // Update the angle state
    setAngle(degrees >= 0 ? degrees : degrees + 360); // Normalize to [0, 360) degrees
  };

  return (
    <div
      style={{
        height: '100vh',
        width: '100vw',
        backgroundColor: '#f0f0f0',
        position: 'relative',
        textAlign: 'center',
      }}
      onMouseMove={handleMouseMove}
    >
      <h1>Angle Between Cursor and Center</h1>
      <h2>{angle.toFixed(2)}Â°</h2>
      <div
        style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          width: '10px',
          height: '10px',
          backgroundColor: 'black',
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
        }}
      />
    </div>
  );
}

export default App;
