import React, { useState } from 'react';
import './App.css'; // Ensure you have styles here


function RotatingLine() {
  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh', // Full viewport height
      position: 'relative',
      backgroundColor: '#f0f0f0', // Optional background color
    }}>
      <div style={{
        width: '200px', // Length of the line
        height: '2px', // Thickness of the line
        backgroundColor: 'blue', // Color of the line
        position: 'absolute',
        top: '50%', // Center vertically
        left: '50%', // Center horizontally
        transformOrigin: '0% 50%', // Rotate around the left end
        animation: 'rotate 2s linear infinite', // Animation properties
      }} />
    </div>
  );
}

export default RotatingLine;
//export default App;
