import React, { useState } from 'react';

function App() {
  const [rotation, setRotation] = useState(0); // State to hold the rotation angle

  const handleMouseMove = (e) => {
    const { clientX, clientY } = e;
    const centerX = window.innerWidth / 2; // X coordinate of the center
    const centerY = window.innerHeight / 2; // Y coordinate of the center

    // Calculate the angle in radians
    const deltaX = clientX - centerX;
    const deltaY = clientY - centerY;
    const radians = Math.atan2(deltaY, deltaX);

    // Convert radians to degrees
    const degrees = (radians * 180) / Math.PI;

    // Normalize to [0, 360) degrees
    setRotation(degrees >= 0 ? degrees : degrees + 360);
  };

  return (
    <div
      style={{
        height: '100vh',
        width: '100vw',
        backgroundColor: '#f0f0f0',
        position: 'relative',
        textAlign: 'center',
      }}
      onMouseMove={handleMouseMove}
    >
      <h1>Set Angle of Rotation</h1>
      <h2>{rotation.toFixed(2)}Â°</h2>
      <div
        style={{
          width: '100px',
          height: '50px',
          backgroundColor: 'blue',
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: `translate(-50%, -50%) rotate(${rotation}deg)`, // Rotate around the center
          transition: 'transform 0.1s ease',
        }}
      />
    </div>
  );
}

export default App;
