import React, { useState, useRef } from 'react';

function Knob() {
    const [rotationHour, setRotationHour] = useState(0);
    const [rotationMin, setRotationMin] = useState(0);
    const [isDragging, setIsDragging] = useState(false);
    const knobRef = useRef(null);

    const handleMouseDown = () => {
        setIsDragging(true);
    };

    const handleMouseUp = () => {
        setIsDragging(false);
    };

    const handleMouseMove = (e, hand) => {
        if (isDragging) {
            if (hand === "h") {
                const newRotation = rotationHour + e.movementX; // Adjust rotation based on mouse movement
                setRotationHour(newRotation);
            } else if (hand === "m") {
                const newRotation = rotationMin + e.movementX; // Adjust rotation based on mouse movement
                setRotationMin(newRotation);
            } else {
                setRotationMin(100);
            }
        }
    };

    return (
        <div
            style={{
                height: '100vh',
                width: '100vw',
                backgroundColor: '#f0f0f0',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
            }}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp} // Stop dragging if mouse leaves the container
        >
            <div
                ref={knobRef}
                onMouseDown={handleMouseDown}
                onMouseMove={(e) => handleMouseMove(e, "h")} // Pass "h" for hour hand
                style={{
                    width: '100px',
                    height: '100px',
                    backgroundColor: '#007bff',
                    borderRadius: '50%', // Make it a circle
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    cursor: 'grab',
                    transform: `rotate(${rotationHour}deg)`, // Apply rotation
                    transition: 'transform 0.1s ease', // Smooth transition for rotation
                }}
            >
                <div style={{
                    width: '10px',
                    height: '10px',
                    backgroundColor: 'white',
                    borderRadius: '50%', // Center knob
                }} />
            </div>
            <div
                ref={knobRef}
                onMouseDown={handleMouseDown}
                onMouseMove={(e) => handleMouseMove(e, "m")} // Pass "m" for minute hand
                style={{
                    width: '80px',
                    height: '80px',
                    backgroundColor: '#ff0000',
                    borderRadius: '50%', // Make it a circle
                    position: 'absolute',
                    top: '30%', // Adjust position as needed
                    left: '30%', // Adjust position as needed
                    cursor: 'grab',
                    transform: `rotate(${rotationMin}deg)`, // Apply rotation
                    transition: 'transform 0.1s ease',
                }}
            >
                <div style={{
                    width: '10px',
                    height: '10px',
                    backgroundColor: 'white',
                    borderRadius: '50%', // Center knob
                }} />
            </div>
        </div>
    );
}

export default Knob;
