import React, { useState, useRef } from 'react';
import './App.css';

function App() {
    const [rotationMin, setRotationMin] = useState(0);
    const [rotationHour, setRotationHour] = useState(0);
    const [isDragging, setIsDragging] = useState(false);
    const [activeHand, setActiveHand] = useState(null); // Track which hand is active

    const handleMouseDown = (hand) => {
        setIsDragging(true);
        setActiveHand(hand); // Set the active hand
    };

    const handleMouseMove = (e) => {
        if (isDragging && activeHand) {
            if (activeHand === "h") {
                const newRotation = rotationHour + e.movementX;
                setRotationHour(newRotation);
            } else if (activeHand === "m") {
                const newRotation = rotationMin + e.movementX;
                setRotationMin(newRotation);
            }
        }
    };

    const handleMouseUp = () => {
        setIsDragging(false);
        setActiveHand(null); // Reset active hand
    };

    const radius = 350;
    const centerRadius = 25;
    const hourLen = radius * 0.5;
    const minLen = radius * 0.75;
    const handWidth = 16;

    return (
        <div
            style={{ height: '100vh', width: '100vw', backgroundColor: 'brown', position: 'relative', textAlign: 'center' }}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp}
        >
            <div style={{ transform: `translateY(-100px)` }}>
                {/* Clock Border */}
                <div style={{
                    width: `${radius * 2 + 20}px`,
                    height: `${radius * 2 + 20}px`,
                    backgroundColor: `black`,
                    borderRadius: `50%`,
                    position: `absolute`,
                    top: `50%`,
                    left: `50%`,
                    transform: `translate(-50%, -50%)`,
                }} />

                {/* Clock */}
                <div style={{
                    width: `${radius * 2}px`,
                    height: `${radius * 2}px`,
                    backgroundColor: `tan`,
                    borderRadius: `50%`,
                    position: `absolute`,
                    top: `50%`,
                    left: `50%`,
                    transform: `translate(-50%, -50%)`,
                }} />

                {/* Hour Hand */}
                <div
                    onMouseDown={() => handleMouseDown("h")}
                    style={{
                        width: `${handWidth}px`,
                        height: `${hourLen}px`,
                        backgroundColor: `black`,
                        borderRadius: `4px`,
                        position: `absolute`,
                        top: `50%`,
                        left: `50%`,
                        transform: `translate(-50%, -100%) rotate(${rotationHour}deg)`,
                        transformOrigin: `50% 100%`,
                    }}
                />

                {/* Minute Hand */}
                <div
                    onMouseDown={() => handleMouseDown("m")}
                    style={{
                        width: `${handWidth}px`,
                        height: `${minLen}px`,
                        backgroundColor: `black`,
                        borderRadius: `4px`,
                        position: `absolute`,
                        top: `50%`,
                        left: `50%`,
                        transform: `translate(-50%, -100%) rotate(${rotationMin}deg)`,
                        transformOrigin: `50% 100%`,
                    }}
                />
            </div>
        </div>
    );
}

export default App;
