import React, { useState } from 'react';
import './App.css'; // Ensure you have styles here

function App() {

	const [backgroundColor, setBackgroundColor] = useState(`brown`);

	const radius = 350
	const centerRadius = 25
	const hourLen = radius * 0.5
	const minLen = radius * 0.75


	return (
        <div style={{ position: `relative`, textAlign: `center`, marginTop: `60px` }}>
            {/* CLOCK */}
            <div
                style={{
                    width: `${2 * radius}px`,
                    height: `${2 * radius}px`,
                    backgroundColor: `tan`,
                    borderRadius: `50%`,
                    border: `10px solid black`,
                    margin: `auto`,
                    transform: `translateY(-50px)`,
                }}
            />

            {/* CLOCK CENTER*/}
            <div
                style={{
                    width: `${2 * centerRadius}px`,
                    height: `${2 * centerRadius}px`,
                    backgroundColor: `black`,
                    borderRadius: `50%`,
                    margin: `auto`,
                    position: `absolute`,
                    top: `${radius - centerRadius}px`,
                    left: `${radius - centerRadius}px`,
                }}
            />

            {/* HOUR HAND */}
            <div
                style={{
                    width: `15px`,
                    height: `${hourLen}px`,
                    backgroundColor: `black`,
                    borderRadius: `4px`,
                    margin: `auto`,
                    position: `absolute`,
                    top: `${(radius - minLen)/2}px`,
                    left: `${(radius - minLen)/2}px`,
                }}
            />
        </div>
	);
}

export default App;
